mapper.select:
/* ScheduleMapper.getScheduleList */
Select * FROM tbl_calendar 
WHERE userno = #{userno}

mapper.update:
/* ScheduleMapper.updateSchedule */
UPDATE tbl_calendar
SET annititle=#{annititle}, annidate=#{annidate}, isholiday=#{isholiday}
WHERE cno=#{cno}

mapper.delete:
/* ScheduleMapper.deleteSchedule */
DELETE FROM
tbl_calendar WHERE cno=#{cno}
